import { UseMutationOptions, useMutation } from "@tanstack/react-query";
import axios from "@/libs/axios";

const ENDPOINT = "/backoffice/conversations/sessions";

// Generated by https://quicktype.io
export interface ConversationsSessionIdEndRequest {
  sessionId: string;
  accessToken: string;
}

// Generated by https://quicktype.io
export interface ConversationsSessionIdEndResponse {
  message?: string;
  error?: string;
}

export const useConversationsSessionIdEnd = (
  options?: UseMutationOptions<
    ConversationsSessionIdEndResponse,
    Error,
    ConversationsSessionIdEndRequest
  >
) => {
  return useMutation({
    mutationFn: async (payload) => {
      const { sessionId, accessToken } = payload;

      const { data } = await axios.post<ConversationsSessionIdEndResponse>(
        ENDPOINT + "/" + sessionId + "/" + "end",
        null,
        {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        }
      );

      return data;
    },
    ...options,
  });
};
